import React from "react";
import "./Form.css";
// import qs from "qs";

function Form() {
  const [product, setProduct] = React.useState({
    name: "",
    picture: "",
  });
  const [allProducts, setAllProducts] = React.useState([]);

  const change = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const submitForm = (e) => {
    e.preventDefault();
    setAllProducts({ ...product, product });
  };

